import React, { Component } from "react";
import Web3 from "web3";
import { contract_address, contract_abi } from "./contract";
import logo from "./logo.svg";
import "./App.css";
import Header from "./components/header/header";

class App extends Component {
  componentWillMount() {
    this.blockchainConnection();
  }

  async blockchainConnection() {
    let web3 = new Web3(Web3.givenProvider);
    let balance = await web3.eth.getBalance(
      "0x32fd8606afbc83d5fc2f196ec689606a9dd69d7a"
    );
    let erc20token = new web3.eth.Contract(contract_abi, contract_address);
    let myTokenAmount = await erc20token.methods
      .balanceOf("0x32fd8606afbc83d5fc2f196ec689606a9dd69d7a")
      .call();
    myTokenAmount = myTokenAmount ? myTokenAmount : 0;
    this.setState({
      balance: parseInt(myTokenAmount)
    });
  }

  constructor(props) {
    super(props);
    this.state = {
      balance: 0
    };
  }

  render() {
    return (
      <div>
        <Header />
        <p>Token Balance: {this.state.balance}</p>
      </div>
    );
  }
}

export default App;
